"""TOKENIZE"""
fri_tokenized = [tokenize.sent_tokenize(tweet) for tweet in fri_tweets]
friday = []
for tweet in fri_tokenized:
    friday.append(tweet[0])


"""SENTIMENT ANALYSIS AND WRITE TO CSV"""
sid = SentimentIntensityAnalyzer()
with open('fri.csv', 'w') as csvfile:
    fieldnames = ['compound', 'neg', 'neu', 'pos', 'tweet']
    writer = csv.DictWriter(csvfile, fieldnames=fieldnames)
    writer.writeheader()
    for tweet in friday:
        print (tweet)
        ss = sid.polarity_scores(tweet)
        ss['tweet'] = tweet
        for k in sorted(ss):
            print('{0}: {1}, '.format(k, ss[k]), end='')
        writer.writerow(ss)
        print ()



