"""TOKENIZE"""
sun_tokenized = [tokenize.sent_tokenize(tweet) for tweet in sun_tweets]
sunday = []
for tweet in sun_tokenized:
    sunday.append(tweet[0])


"""SENTIMENT ANALYSIS AND WRITE TO CSV"""
sid = SentimentIntensityAnalyzer()
with open('sun.csv', 'w') as csvfile:
    fieldnames = ['compound', 'neg', 'neu', 'pos', 'tweet']
    writer = csv.DictWriter(csvfile, fieldnames=fieldnames)
    writer.writeheader()
    for tweet in sunday:
        print (tweet)
        ss = sid.polarity_scores(tweet)
        ss['tweet'] = tweet
        for k in sorted(ss):
            print('{0}: {1}, '.format(k, ss[k]), end='')
        writer.writerow(ss)
        print ()



