"""TOKENIZE"""
tue_tokenized = [tokenize.sent_tokenize(tweet) for tweet in tue_tweets]
tuesday = []
for tweet in tue_tokenized:
    tuesday.append(tweet[0])


"""SENTIMENT ANALYSIS AND WRITE TO CSV"""
sid = SentimentIntensityAnalyzer()
with open('tue.csv', 'w') as csvfile:
    fieldnames = ['compound', 'neg', 'neu', 'pos', 'tweet']
    writer = csv.DictWriter(csvfile, fieldnames=fieldnames)
    writer.writeheader()
    for tweet in tuesday:
        print (tweet)
        ss = sid.polarity_scores(tweet)
        ss['tweet'] = tweet
        for k in sorted(ss):
            print('{0}: {1}, '.format(k, ss[k]), end='')
        writer.writerow(ss)
        print ()



